  if (global.v8debug.Debug) {
    var d = global.v8debug.Debug;
    d.setBreakOnException();
    d.setListener(function (e,state) {
      var scopes = [];
      for (var i=0; i < state.frame(0).scopeCount(); ++i) {
        scopes.push(state.frame(0).scope(i).details_.details_[1]);
      }
      console.log(scopes);
    });
  }
